    function generateHistogram(stickData, switchData) {
      const svg = d3.select('#histogram');
      svg.selectAll('*').remove();
  
      const margin = { top: 20, right: 30, bottom: 40, left: 40 },
            width = +svg.attr('width') - margin.left - margin.right,
            height = (+svg.attr('height') - margin.top - margin.bottom - 50) / 2;
  
      const xStick = d3.scaleLinear().domain([0, 1]).range([0, width]);
      const xSwitch = d3.scaleLinear().domain([0, 1]).range([0, width]);
  
      const histogram = d3.histogram().domain(xStick.domain()).thresholds(xStick.ticks(15));
  
      const binsStick = histogram(stickData);
      const binsSwitch = histogram(switchData);
  
      const yStick = d3.scaleLinear().domain([0, d3.max(binsStick, d => d.length)]).range([height, 0]);
      const ySwitch = d3.scaleLinear().domain([0, d3.max(binsSwitch, d => d.length)]).range([height, 0]);
  
      // Stick Strategy Histogram
      const gStick = svg.append('g').attr('transform', `translate(${margin.left},${margin.top})`);
  
      gStick.append('g').attr('class', 'axis axis--x').attr('transform', `translate(0,${height})`).call(d3.axisBottom(xStick));
      gStick.append('g').attr('class', 'axis axis--y').call(d3.axisLeft(yStick));
  
      gStick.selectAll('.bar-stick')
          .data(binsStick)
          .enter().append('rect')
          .attr('class', 'bar-stick')
          .attr('x', d => xStick(d.x0))
          .attr('y', d => yStick(d.length))
          .attr('width', d => xStick(d.x1) - xStick(d.x0) - 1)
          .attr('height', d => height - yStick(d.length))
          .attr('fill', 'steelblue')
          .attr('stroke', 'black')
          .attr('opacity', 0.6);
  
      gStick.append('text')
          .attr('x', width / 2)
          .attr('y', height + margin.bottom)
          .attr('text-anchor', 'middle')
          .text('Winning Proportion');
  
      gStick.append('text')
          .attr('transform', 'rotate(-90)')
          .attr('y', 0 - margin.left)
          .attr('x', 0 - (height / 2))
          .attr('dy', '1em')
          .attr('text-anchor', 'middle')
          .text('Frequency');
  
      gStick.append('text')
          .attr('x', width / 2)
          .attr('y', -10)
          .attr('text-anchor', 'middle')
          .text('Bootstrap Results of Stick Strategy');
  
      // Switch Strategy Histogram
      const gSwitch = svg.append('g').attr('transform', `translate(${margin.left},${margin.top + height + margin.bottom + 50})`);
  
      gSwitch.append('g').attr('class', 'axis axis--x').attr('transform', `translate(0,${height})`).call(d3.axisBottom(xSwitch));
      gSwitch.append('g').attr('class', 'axis axis--y').call(d3.axisLeft(ySwitch));
  
      gSwitch.selectAll('.bar-switch')
          .data(binsSwitch)
          .enter().append('rect')
          .attr('class', 'bar-switch')
          .attr('x', d => xSwitch(d.x0))
          .attr('y', d => ySwitch(d.length))
          .attr('width', d => xSwitch(d.x1) - xSwitch(d.x0) - 1)
          .attr('height', d => height - ySwitch(d.length))
          .attr('fill', 'orange')
          .attr('stroke', 'black')
          .attr('opacity', 0.6);
  
      gSwitch.append('text')
          .attr('x', width / 2)
          .attr('y', height + margin.bottom)
          .attr('text-anchor', 'middle')
          .text('Winning Proportion');
  
      gSwitch.append('text')
          .attr('transform', 'rotate(-90)')
          .attr('y', 0 - margin.left)
          .attr('x', 0 - (height / 2))
          .attr('dy', '1em')
          .attr('text-anchor', 'middle')
          .text('Frequency');
  
      gSwitch.append('text')
          .attr('x', width / 2)
          .attr('y', -10)
          .attr('text-anchor', 'middle')
          .text('Bootstrap Results of Switch Strategy');
    }
  
    function updateHistogram() {
      const stickData = data[selectedN].stick;
      const switchData = data[selectedN].switch;
      generateHistogram(stickData, switchData);
    }
  
    onMount(() => {
      updateHistogram();
    });
  </script>
  
  <div style="text-align: center;">
